import React,{useState} from 'react';
import Card from './component/Card';
import './App.css';
import Form from './component/Form';

export interface Person {
  name: string
  lastName: string
  age: number
  employed: boolean
  description?: string,
  avatar: string
}

const INITIAL_STATE: Array<Person> = [
  {
    name: 'Harry',
    lastName: 'Barzola',
    age: 35,
    employed: true,
    avatar: 'https://i.pravatar.cc/150?u=sergio_serrano'
  },
  {
    name: 'Lore',
    lastName: 'Barzola',
    age: 35,
    employed: true,
    avatar: 'https://i.pravatar.cc/150?u=sergio_serrano'
  },
  {
    name: 'Ana',
    lastName: 'Barzola',
    age: 35,
    employed: true,
    avatar: 'https://i.pravatar.cc/150?u=sergio_serrano'
  },

]

function App() {

  const [persons, setPersons] = useState <Array<Person>>(INITIAL_STATE)

  const handlePerson = (name: string) =>{
    const filteredPerson = persons.filter(person => person.name !== name);
    setPersons(filteredPerson);
  }

  const addPerson = (person:Person):void =>{
    setPersons([...persons, person]);
  }

  return (
    <div className="App">
      {
        persons.map(person=>
        <Card person={person} handlePerson={handlePerson} />
        )
      }
      <Form addPerson={addPerson} />

    </div>
  );
}

export default App;
