import React from 'react';
import Card from './component/Card';
import Form from './component/Form';
import {useCartContext} from './context/PersonContext';
import './App.css';


function App() {

  const {persons, deletePerson, addPerson } = useCartContext();
  return (
    <div className="App">
      {
        persons.map(person =>
        <Card person={person} deletePerson={deletePerson} />
        )
      }
      <Form addPerson={addPerson} />

    </div>
  );
}

export default App;
