import React from 'react';
import Card from './component/Card';
import Form from './component/Form';
import {usePersonContext, PersonProvider} from './context/PersonContext';
import './App.css';


function App() {

  const {persons, deletePerson, addPerson} = usePersonContext();
  return (
    <PersonProvider>
      <div className="App">
        {
          persons.map(person =>
          <Card person={person} deletePerson={deletePerson} />
          )
        }
        <Form addPerson={addPerson} />
      </div>
    </PersonProvider>
  );
}

export default App;
