import React,{useState} from 'react';
import Card from './component/Card';
import './App.css';

export interface Person {
  name: string
  lastName: string
  age: number
  employed: boolean
  description?: string,
  avatar: string
}

const INITIAL_STATE: Person = {
  name: 'Harry',
  lastName: 'Barzola',
  age: 35,
  employed: true,
  avatar: 'https://i.pravatar.cc/150?u=sergio_serrano'
}

function App() {

  const [persons, setPersons] = useState <Array<Person>>([INITIAL_STATE])

  const handlePerson = () =>{
     setPersons({...persons, avatar: 'hi'})
  }
  return (
    <div className="App">
      <Card person={persons} handlePerson={handlePerson}/>

    </div>
  );
}

export default App;
