import React,{useState} from 'react';
import { Person } from "../App";

interface FormProps {
    persons: Array<Person>
    addPerson: ()=>void
}
const Form = ({addPerson , persons} :FormProps): JSX.Element=> {

    const InitialState: Person ={
        name:'',
        lastName: '',
        age: 0,
        description: '',
        employed: false,
        avatar: ''
    }

    const [inputValue, setInputValue] = useState <Person>(InitialState)

    const handleSubmit = () => {}
    const handleChange = (evt: React.ChangeEvent<HTMLInputElement>) => {
        setInputValue({
            ...inputValue,
            [evt.target.name]: evt.target.value
        })
    }

    const add = () => {
        setPersons([...persons, inputValue]);
        setInputValue(InitialState)
    }
    return(
        <div>
            <form onSubmit={handleSubmit}>
                <input 
                    value={inputValue.name} 
                    onChange={handleChange}
                    name='name' 
                    type='text' 
                    placeholder='Name'
                />
                <input 
                    value={inputValue.lastName} 
                    onChange={handleChange}
                    name='lastName' 
                    type='text' 
                    placeholder='LastName'
                />
                <input 
                    value={inputValue.age} 
                    onChange={handleChange}
                    name='age' 
                    type='number' 
                    placeholder='Age'
                />

            </form>
            <button onClick={add}>Add Person</button>
        </div>
    )
};

export default Form;
